var Engine = Engine || {};

//options container
Engine.Options = function () {
    var options = {
        baseUrl: null,
        locale: null
    };

    return {
        /**
         * @param string key
         * @returns mixed
         */
        getParam: function (key) {
            return options[key] ? options[key] : null;
        },

        /**
         * @param string key
         * @param mixed value
         */
        setParam: function (key, value) {
            options[key] = value;
        },

        /**
         * init
         */
        getBaseUrl: function () {
            var locale = $('meta[name="lang"]').attr('content');
            if (locale) {
                return '/' + locale;
            }

            return '';
        }
    }
}();

//plaform component
Engine.Platform = {};

//loader
Engine.Platform.Loader = function () {
    return {
        show: function () {
            if ($('#loader').size() == 0) {
                var text = $('meta[name="application-name"]').attr('content');
                var letters = [];
                for (var i = 0; i < text.length; i++) {
                    letters.push('<div id="fountainTextG_' + (i + 1) + '" class="fountainTextG">' + text[i] + '</div>');
                }
                $(document.body).append('<div class="modal fade" style="z-index:1000000;" id="loader" tabindex="-1" role="dialog" aria-labelledby="myModalLabel7" aria-hidden="true"><div id="fountainTextG">' + letters.join('') + '</div></div>');
            }
            $('#loader').modal();
        },

        hide: function () {
            $('#loader').modal('hide');
        }
    }
}();

Engine.Platform.Form = function () {
    return {
        submitAjax: function (form, callback) {
            Platform.Loader.show();
            Platform.Form.removeErrorMessages(form);

            $.ajax({
                url: $(form).attr('action'),
                type: 'POST',
                data: $(form).serialize(),
                dataType: 'json',
                success: function (response) {
                    Platform.Loader.hide();
                    if (callback) {
                        callback.call(window, response);
                    }
                },
                error: function (response) {
                    Platform.Form.processFormErrors($(form), response);
                    Platform.Loader.hide();
                }
            });
        },

        removeErrorMessages: function (form) {
            $(form).find('.has-error').removeClass('has-error');
            $(form).find('.help-has-error').remove();
        },

        processFormErrors: function (form, response) {
            Platform.Form.processErrors(form, response.responseJSON);
        },

        processErrors: function (form, errors) {
            $.each(errors, function (key, field) {
                var f = form.find('#' + key);
                if (typeof field == 'string') {
                    f.parent().addClass('has-error');
                    $('<span class="help-block help-has-error"><em></em>' + field + '</span>').insertAfter(f);
                } else if ($.isArray(field)) {
                    f.parent().addClass('has-error');
                    $('<span class="help-block help-has-error"><em></em>' + field.join('<br/>') + '</span>').insertAfter(f);
                } else {
                    Platform.Form.processErrors(form, field);
                }

                f.one('focus', function () {
                    f.parent().removeClass('has-error');
                    f.parent().find('.help-block').hide();
                });
            });
        }
    }
}();


Engine.Platform.Popup = function () {
    return {
        show: function (url, params, callback) {
            Platform.Loader.show();

            $.ajax({
                url: url,
                type: 'GET',
                data: params || {},
                cache: false,
                success: function (response) {
                    Platform.Loader.hide();
                    Platform.Popup.create(response, callback);
                }
            });
        },

        create: function (html, callback) {
            var modal = $(html),
                id = modal.attr('id');
            $(document.body).find('#' + id).remove();
            $(document.body).append(modal);

            var modal = $('#' + id);
            setTimeout(function () {
                modal.modal({show: false});
                modal.on('hidden.bs.modal', function () {
                    Platform.Popup.close(id);
                });
                if (callback) {
                    callback.call(window, modal);
                }
                modal.modal('show');
            }, 500);
        },

        close: function (id) {
            $('#' + id).remove();
            $('.modal-backdrop').remove();
        }
    }
}();

//@deprecated
