var Review = function () {
    var ct = $('[data-role="reviews-container"]');
    var product = ct.data('product');
    var baseUrl = '/reviews';
    var rating;

    return {
        init: function () {
            rating = $('[data-role="rating"]');

            Review.initPaging();
            Review.initForm();
        },

        initForm: function () {
            rating.find('a').click(function(){
                rating.find('a').removeClass('active');
                var stars = $(this).attr('class').replace('star','');
                $(this).addClass('active');
                rating.find('input').val(stars);
                return false;
            });
        },

        submitForm: function(form) {
            Platform.Form.submitAjax(form, function(response) {
                $('#review-window').modal();

                //reset form
                $(form)[0].reset();
                rating.find('a').removeClass('active');
            });
        },

        initPaging: function () {
            var paging = ct.find('.paging');
            paging.find('a').click(function () {
                Review.load($(this).attr('href'));
                return false;
            });
        },

        load: function (url) {
            if (ct.size() > 0) {
                $.ajax({
                    url: url || (baseUrl + '/' + product),
                    success: function (html) {
                        ct.html(html);
                        Review.initPaging();
                    }
                })
            }
        }
    }
}();

$(function () {
    Review.load();
});


var Shop = {};

Shop.ListProduct = function () {
    var defaults = {
        selector: '[data-role="variation-select"]',
        item: '.grid-item',
        image: '.grid-item-img > img',
        price: '.grid-item-main-price',
        oldprice: '.grid-item-old-price'
    };
    return {
        init: function (config) {
            defaults = $.extend(defaults, config || {});
            //select variations
            $(defaults.item).find(defaults.selector).change(function () {
                Shop.ListProduct.changeVariation($(this).val());
            });
        },

        changeVariation: function (id) {
            var info = $('meta[itemid="' + id + '"]');
            var productCt = info.parents(defaults.item);

            //change image
            productCt.find(defaults.image).attr('src', info.attr('image'));

            //prices
            var oldprice = info.attr('oldprice'),
                price = info.attr('price');

            productCt.find(defaults.oldprice).html(oldprice);
            productCt.find(defaults.price).html(price);
            if (oldprice) {
                productCt.find(defaults.price).addClass('promo-price');
            } else {
                productCt.find(defaults.price).removeClass('promo-price');
            }
        }
    }
}();

Shop.Product = function () {
    var variations = $('[data-role="variation"]'),
        select = $('[data-role="variations-select"]'),
        images = $('[data-role="variation-image"]'),
        priceCt = $('[data-role="price"]'),
        priceOldCt = $('[data-role="old-price"]'),
        codeCt = $('[data-role="code"]'),
        expectCt = $('[data-role="expect-delivery"]'),
        buyCt = $('[data-role="buy"]');

    var gallery = $('[data-role="gallery"]'),
        galleryPreview = $('[data-role="gallery-preview"]');

    var sync1 = $("#sync1");
    var sync2 = $("#sync2");
    var sync3 = $("#sync3");
    return {
        init: function () {
            Shop.Product.initGallery();
            Shop.Product.initGalleryVariations();
            Shop.Product.initVariations();
        },

        //select by images
        initGalleryVariations: function () {
            images.click(function () {
                Shop.Product.changeVariation($(this).data('id'), $(this));
            });
        },

        //select variations
        initVariations: function () {
            //clicks on gallery
            variations.click(function () {
                //active
                variations.parent().removeClass('active');
                $(this).parent().addClass('active');

                Shop.Product.changeVariation($(this).data('id'));

                return false;
            });

            //custom select
            select.selectpicker();

            //select box changes
            select.change(function () {
                Shop.Product.changeVariation($(this).val());
            });

            //select variation from url
            var slug = document.location.hash.replace('#', '');
            if (slug) {
                select.val($('[data-slug="' + slug + '"]').data('id')).trigger('change');
            }
        },

        //change data
        changeVariation: function (id, img) {
            var info = variations.filter('[data-id="' + id + '"]');

            //active
            variations.parents().removeClass('active');
            info.parent().addClass('active');

            //price
            priceCt.html(info.data('price'));

            var oldPrice = info.data('old-price');
            priceOldCt.html(oldPrice);
            if (oldPrice != 0) {
                priceOldCt.show();
                priceCt.addClass('promo-price');
            } else {
                priceOldCt.hide();
                priceCt.removeClass('promo-price');
            }

            //select variation in list
            select.selectpicker('val', id);

            //change code
            codeCt.html(info.data('code'));

            //set id to expected delivery
            expectCt.find('[data-variation]').attr('data-variation', id);

            //change expect or buy
            if (info.data('warehouse')) {
                expectCt.hide();
                buyCt.show();
            } else {
                expectCt.show();
                buyCt.hide();
            }

            //select in gallery
            var index = gallery.find(img || '[data-role="variation-image"][data-id="' + id + '"]').parent().index();
            sync3.data("owlCarousel").goTo(index);
            if (img == undefined) {
                sync1.data("owlCarousel").goTo(index);
                sync2.data("owlCarousel").goTo(index);
            }

            //change url
            document.location.hash = '#' + info.data('slug');
        },

        initGallery: function () {
            sync1.owlCarousel({
                singleItem: true,
                slideSpeed: 1000,
                navigation: false,
                pagination: false,
                afterAction: syncPosition,
                responsiveRefreshRate: 200,
                transitionStyle: 'fadeUp'
            });

            sync2.owlCarousel({
                items: 4,
                itemsDesktop: [1199, 4],
                itemsDesktopSmall: [979, 4],
                itemsTablet: [768, 4],
                itemsMobile: [479, 4],
                pagination: false,
                navigation: true,
                responsiveRefreshRate: 100,
                afterInit: function (el) {
                    el.find(".owl-item").eq(0).addClass("synced");
                }
            });

            sync3.owlCarousel({
                navigation: true, // Show next and prev buttons
                slideSpeed: 400,
                paginationSpeed: 400,
                singleItem: true
            });

            function syncPosition(el) {
                var current = this.currentItem;
                sync2
                    .find(".owl-item")
                    .removeClass("synced")
                    .eq(current)
                    .addClass("synced");
                if (sync2.data("owlCarousel") !== undefined) {
                    Shop.Product.centerImage(current)
                }
            }

            sync2.on("click", ".owl-item", function (e) {
                e.preventDefault();
                var number = $(this).data("owlItem");
                sync1.trigger("owl.goTo", number);
                sync3.trigger("owl.goTo", number);
            });
        },

        centerImage: function (number) {
            var sync2visible = sync2.data("owlCarousel").owl.visibleItems;
            var num = number;
            var found = false;
            for (var i in sync2visible) {
                if (num === sync2visible[i]) {
                    var found = true;
                }
            }

            if (found === false) {
                if (num > sync2visible[sync2visible.length - 1]) {
                    sync2.trigger("owl.goTo", num - sync2visible.length + 2)
                } else {
                    if (num - 1 === -1) {
                        num = 0;
                    }
                    sync2.trigger("owl.goTo", num);
                }
            } else if (num === sync2visible[sync2visible.length - 1]) {
                sync2.trigger("owl.goTo", sync2visible[1])
            } else if (num === sync2visible[0]) {
                sync2.trigger("owl.goTo", num - 1)
            }
        }
    }
}();


//Handle life search for LifeSearchType
Shop.Search = function () {
    return {
        init: function () {
            $('[data-role="life-search"]').each(function () {
                var input = $(this);

                var searchCount;
                var searchUrl;
                var searchstr;

                input.autocomplete({
                    minLength: 3,
                    source: function (request, response) {
                        $.ajax({
                            url: input.data('url'),
                            data: {
                                query: input.val()
                            },
                            dataType: "json",
                            success: function (result) {
                                searchCount = result.count;
                                searchUrl = result.search || '';
                                searchStr = result.query || '';

                                response($.each(result.products, function (i, product) {
                                    return product;
                                }));
                            }
                        });
                    },
                    open: function () {
                        $(".ui-autocomplete").addClass('livesearch').append('<a href="' + searchUrl + '" class="livesearch-all-results">  (' + searchCount + ')<em class="i-more"></em></a>');
                    }
                });

                //custom renderer
                input.autocomplete().data("ui-autocomplete")._renderItem = function (ul, item) {
                    return $('<li>')
                        .append('<a href="' + item.url + '" class="live-item"><img src="' + item.image + '"><span>' + item.name + '</span></a>')
                        .appendTo(ul);
                };
            });
        }
    }
}();


