var Cart = function () {
    return {
        init: function () {
            Cart.initBuyButtons();
            Cart.initCart();
        },

        //init buy buttons on products
        initBuyButtons: function () {
            $('[data-role="product-buy"]').click(function () {
                var btn = $(this);

                Platform.Loader.show();
                var product = $(this).data('product');
                var variation = $(this).parents('[data-role="buy-ct"]').find('[data-role="variation-select"]').val();
                if (!variation) {
                    variation = $(this).data('variation');
                }
                var extra = $(this).data('extra');
                $.ajax({
                    url: '/cart/add/' + product,
                    type: 'POST',
                    data: $.extend(extra || {}, {variation: variation}),
                    success: function (response) {
                        //handle tag manager
                        TagManager.Checkout.handleAddToCart(btn.closest('[data-role="product"]'));

                        Platform.Loader.hide();
                        Platform.Popup.create(response);
                    }
                });
            });
        },

        //init buy buttons on products in popups
        initPopupBuyButtons: function (callback) {
            $('#cart-success [data-role="product-buy"]').click(function () {
                var btn = $(this);

                Platform.Loader.show();

                var product = $(this).data('product');
                var variation = $(this).parents('[data-role="buy-ct"]').find('[data-role="variation-select"]').val();
                if (!variation) {
                    variation = $(this).data('variation');
                }
                var extra = $(this).data('extra');
                $.ajax({
                    url: '/cart/add/' + product,
                    type: 'POST',
                    data: $.extend(extra || {}, {variation: variation}),
                    success: function (response) {
                        Platform.Loader.hide();
                        //notify google analytics event
                        if (callback) {
                            callback.call();
                        }
                    }
                });
            });
        },

        changeCount: function (count) {
            if (count > 0) {
                $('[data-role="cart-count"]').text(count).show();
            } else {
                $('[data-role="cart-count"]').hide();
            }
        },

        goTo: function () {
            var count = $('[data-role="cart-count"]').text();
            if (count > 0) {
                document.location.href = '/cart';
            }

            return false;
        },

        //add to cart
        addToCart: function (form) {
            Platform.Loader.show();
            $.ajax({
                url: $(form).attr('action'),
                type: 'POST',
                data: $(form).serialize(),
                success: function (response) {
                    //handle tag manager
                    TagManager.Checkout.handleAddToCart($(form).closest('[data-role="product"]'));

                    //loader
                    Platform.Loader.hide();

                    //show popup
                    Platform.Popup.create(response, function () {
                        $(".items-slider").owlCarousel(
                            {
                                navigation: true, // Show next and prev buttons
                                slideSpeed: 400,
                                paginationSpeed: 400,
                                itemsCustom: [
                                    [0, 1],
                                    [480, 2],
                                    [720, 3],
                                    [960, 4],
                                    [1200, 5]
                                ],
                                pagination: false,
                                scrollPerPage: true
                            }
                        );
                    });
                }
            });
        },

        initSpecialOffersSlider: function (context) {
            $(context + ' .next-quitz').click(function() {
                var $current = $(this).closest('.quitz-block-small');
                var $next = $current.next(context + ' .quitz-block-small:first');

                if (!$next.length) {
                    $next = $(context + ' .quitz-block-small:first');
                }

                $next.removeClass('hidden');
                $current.addClass('hidden');
                
                return false;
            });
        },

        initCart: function () {
            var citySelect = $('[data-role="cart-city"]');
            citySelect.select2({
                minimumResultsForSearch: 10,
                dropdownAutoWidth: "true"
            });

            citySelect.change(function () {
                $.ajax({
                    type: 'PATCH',
                    url: '/cart/change/city',
                    data: {
                        city: $(this).val()
                    },
                    complete: function () {
                        Cart.updateCart();
                    }
                });
            });

            Cart.initCartActions();
            this.initSpecialOffersSlider('#cart_ct');
        },

        initPopup: function () {
            var citySelect = $('[data-role="cart-city"]');
            citySelect.select2({
                minimumResultsForSearch: 10,
                dropdownAutoWidth: "true"
            });

            citySelect.change(function () {
                $.ajax({
                    type: 'PATCH',
                    url: '/cart/change/city',
                    data: {
                        city: $(this).val()
                    },
                    complete: function () {
                        Cart.updatePopup();
                    }
                });
            });
            $(".modal-content .items-slider").owlCarousel(
                {
                    navigation: true, // Show next and prev buttons
                    slideSpeed: 400,
                    paginationSpeed: 400,
                    itemsCustom: [
                        [0, 1],
                        [480, 2],
                        [720, 3],
                    ],
                    pagination: false,
                    scrollPerPage: true
                }
            );
            this.initPopupBuyButtons();
            this.initSpecialOffersSlider('#cart-success');
        },

        updatePopup: function () {
            $.ajax({
                url: '/cart/popup/offers',
                complete: function (response) {
                    $('[data-role="offers"]').html(response.responseText);
                }
            });
        },

        updateCart: function (response) {
            var update = function (html) {
                $('#cart_ct').html(html).trigger('load');
                //apply select picker
                $('.selectpicker').selectpicker();
                $(".items-slider").owlCarousel(
                    {
                        navigation: true, // Show next and prev buttons
                        slideSpeed: 400,
                        paginationSpeed: 400,
                        itemsCustom: [
                            [0, 1],
                            [480, 2],
                            [720, 3],
                            [960, 4],
                            [1200, 5]
                        ],
                        pagination: false,
                        scrollPerPage: true
                    }
                );
                //init cart
                Cart.initCart();
            }

            if (response) {
                update(response);
            } else {
                $.ajax({
                    url: '/cart/products',
                    success: function (response) {
                        update(response);
                    }
                });
            }
        },

        //init cart actions oparetions
        initCartActions: function () {
            var rows = $('[data-role="row"]');

            //change
            rows.find('[data-role="cart-product-count"]').on('change', function () {
                var count = $(this).val();
                var rowId = $(this).closest('tr').data('id');

                Cart.changeQuantity(rowId, count);
            });

            //remove
            rows.find('[data-role="delete-product"]').on('click', function () {
                var rowId = $(this).closest('tr').data('id');
                Cart.removeProduct(rowId);
            });

            //remove
            rows.find('[data-role="variation-select"]').on('change', function () {
                var rowId = $(this).closest('tr').data('id');
                Cart.changeVariation(rowId, $(this).val());
            });
        },

        //change quantity
        changeQuantity: function (rowId, count) {
            Platform.Loader.show();

            $.ajax({
                type: 'PATCH',
                url: '/cart/change/' + rowId,
                data: {
                    count: count
                },
                success: function (response) {
                    Cart.updateCart(response);
                    Platform.Loader.hide();
                }
            });
        },

        //change quantity
        changeVariation: function (rowId, variation) {
            Platform.Loader.show();

            $.ajax({
                type: 'PATCH',
                url: '/cart/change/variation/' + rowId,
                data: {
                    variation: variation
                },
                success: function (response) {
                    Cart.updateCart(response);
                    Platform.Loader.hide();
                }
            });
        },

        //remove product
        removeProduct: function (rowId) {
            Platform.Loader.show();

            //handle tag manager
            TagManager.Checkout.handleRemoveFromCart($('[data-id="' + rowId + '"]'));

            $.ajax({
                type: 'DELETE',
                url: '/cart/delete/' + rowId,
                success: function (response) {
                    Cart.updateCart(response);
                    Platform.Loader.hide();
                }
            });
        }
    }
}();

$(function () {
    Cart.init();
});


var Checkout = function () {
    var checkoutCt = $('#checkout_ct');
    var buyButtons = $('[data-role="product-buy"]');
    var elements = ['city', 'delivery_service', 'payment_type', 'delivery_point'];
    var productsCt = $('[data-role="products-ct"]');
    var phone = $('[data-role="phone"]');

    return {
        init: function () {
            if (checkoutCt.size() == 0) {
                return;
            }

            Checkout.initSelect(true);
            Checkout.initPhone();
            Checkout.updateData();
        },

        initPhone: function () {
            phone.mask('38 (999) 999-99-99', {
                fixedChars: '[() -]',
                autoTab: false
            });
        },

        initSelect: function (bindEvents) {
            $.each(elements, function (i, key) {
                var input = $('[data-role="' + key + '"]');

                if (bindEvents === true) {
                    if (key == 'city' || key == 'delivery_service') {
                        input.change(function () {
                            Checkout.updateData(key);
                        });
                    }
                }
            });
        },

        updateData: function (key) {
            Platform.Loader.show();

            var data = {};
            data.city = $('[data-role="city"]').val();
            if (key == 'delivery_service') {
                data.delivery_service = $('[data-role="delivery_service"]').val();
            }

            $.ajax({
                url: '/checkout/delivery',
                data: data,
                dataType: 'json',
                success: function (response) {
                    $.each(response.lists, function (key, values) {
                        var options = [];
                        var select = $('[data-role="' + key + '"]');
                        $.each(values, function (i, value) {
                            var dataAttr = '';
                            if (key == 'delivery_service') {
                                dataAttr = 'data-address="' + value.askAddress + '"';
                            }

                            options.push('<option data-slug="' + value.slug + '" ' + dataAttr + ' value="' + value.id + '">' + value.name + '</option>');
                        });
                        select.html(options.join(''));
                    });

                    $.each(response.values, function (key, value) {
                        var input = $('[data-role="' + key + '"]');
                        if (value) {
                            input.val(value);
                        }
                    });

                    //delivery and total
                    $('[data-role="delivery-info"]').replaceWith(response.html);

                    Checkout.initSelect();
                    Checkout.checkDeliveryFields();

                    Platform.Loader.hide();
                },
                error: function() {
                    Platform.Loader.hide();
                }
            });
        },

        checkDeliveryFields: function () {
            var askAddress = $('[data-role="delivery_service"]').find('option:selected').data('address');
            //delivery
            if (askAddress == true) {
                $('[data-role="address"]').closest('.form-group').show();
                $('[data-role="delivery_point"]').val('').closest('.form-group').hide();
            } else {
                $('[data-role="address"]').val('').closest('.form-group').hide();
                $('[data-role="delivery_point"]').closest('.form-group').show();
            }
        },

        submitForm: function (form, callback) {
            Platform.Form.submitAjax(form, function (response) {
                //handle tag manager
                TagManager.Checkout.handleCheckoutCheckout();

                //redirect
                setTimeout(function () {
                    document.location.href = '/checkout/success';
                }, 250);
            });
        }
    }
}();

$(function () {
    Checkout.init();
});


var SpecialOffer = function () {
    return {
        init: function () {
            $('[data-role="product-buy-complect"]').click(function () {
                SpecialOffer.openBuyComplectPopup($(this));
            });

            $('[data-role="special-offers-select"]').click(function () {
                SpecialOffer.openSpecialOffersPopup($(this));
            });
            $('#cart_ct').on('load', function(){
                $('[data-role="special-offers-select"]').click(function () {
                    SpecialOffer.openSpecialOffersPopup($(this));
                });
            });
        },

        openBuyComplectPopup: function (btn) {
            var productA = btn.data('product-a'),
                productB = btn.data('product-b'),
                specialOffer = btn.data('special-offer');
            Platform.Popup.show('/special-offers/buy', {
                'product-a': productA,
                'product-b': productB,
                'special-offer': specialOffer
            });
        },

        buyComplect: function (btn) {
            var productA = btn.data('product-a'),
                productB = btn.data('product-b'),
                variationA = btn.parents('.modal').find('[data-role="variation-a-select"]').val(),
                variationB = btn.parents('.modal').find('[data-role="variation-b-select"]').val(),
                specialOffer = btn.data('special-offer');

            $.ajax({
                url: '/cart/add/' + productB,
                type: 'POST',
                data: {
                    'variation': variationB,
                    'product_a': productA,
                    'variation_a': variationA,
                    'special_offer': specialOffer
                },
                complete: function (response) {
                    btn.parents('.modal').modal('hide');
                    Platform.Loader.hide();
                    Platform.Popup.show('/cart/popup');
                }
            });
        },

        openSpecialOffersPopup: function (btn) {
            var selected = btn.data('order-product');
            Platform.Popup.show('/special-offers/choose/'+selected);
        }
    }
}();

